import React, { useState } from "react";
import classNames from "classnames";
import GeneralHeader from "../components/common/GeneralHeader";
import Banner5 from "../components/banner/banner5/Banner5";
import ImageBox from "../components/about/ImageBox";
import About2 from "../components/about/About2";
import FunFactsThree from "../components/other/funfacts/FunFactsThree";
import { Link } from "react-router-dom";
import { FiPlus } from "react-icons/fi";
import SectionsHeading from "../components/common/SectionsHeading";
import Testimonial from "../components/sliders/Testimonial";
import HowItWork4 from "../components/hiw/HowItWork4";
import TeamOne from "../components/team/TeamOne";
import Button from "../components/common/Button";
import NewsLetter from "../components/other/cta/NewsLetter";
import Footer from "../components/common/footer/Footer";
import ScrollTopBtn from "../components/common/ScrollTopBtn";
import Breadcrumb from "../components/common/Breadcrumb";
import breadcrumbimg from "../assets/images/bread-bg.jpg";
import gitlogo from "../assets/images/git-logo.png";
import { FiRefreshCw } from "react-icons/fi";
import sectiondata from "../store/store";
import Select from "react-select";

const state = {
  breadcrumbimg: breadcrumbimg,
};

const topics = [
  "Official Management District",
  "Japanese Architecture",
  "Ear-acupuncture",
  "Cupping",
  "Scalp Acupuncture",
  "Reflexology",
  "Tuina",
  "Reiki",
  "Tibetan Medicine",
  "Pathology",
  "Technical Issues",
];

const tableRows = {
  "Official Management District": [
    {
      topic: "Let's test the site",
      by: "admin",
      on: "2017-04-05 08:59:15",
      views: "-",
      src: "http://tcmfiles.com//admincp/images/ai-brain.jpg",
    },
    {
      topic: "add announcement",
      by: "admin",
      on: "2020-08-19 22:14:06",
      views: "-",
      src: "http://tcmfiles.com//admincp/images/ai-brain.jpg",
    },
  ],
  "Japanese Architecture": [],
  "Ear-acupuncture": [
    {
      topic: "topic in ear-acu",
      by: "admin",
      on: "2020-08-22 20:36:34",
      views: "-",
      src: "http://tcmfiles.com//admincp/images/ai-brain.jpg",
    },
  ],
  Cupping: [],
  "Scalp Acupuncture": [],
  Reflexology: [],
  Tuina: [],
  Reiki: [
    {
      topic: "moving from tuina to reiki",
      by: "admin",
      on: "2020-08-22 20:36:34",
      views: "-",
      src: "http://tcmfiles.com//admincp/images/avatar27042020.PNG",
    },
  ],
  "Tibetan Medicine": [],
  Pathology: [],
  "Technical Issues": [
    {
      topic: "test",
      by: "admin",
      on: "2020-08-22 20:36:34",
      views: "-",
      src: "http://tcmfiles.com//admincp/images/ai-brain.jpg",
    },
  ],
};

function About() {
  const [selectedTopic, setSelectedTopic] = useState(
    "Official Management District"
  );
  const [isAllChecked, setIsAllChecked] = useState(false);

  const handleTopicClick = (e) => setSelectedTopic(e.target.name);

  return (
    <main className="about-page">
      {/* Header */}
      <GeneralHeader />
      <Breadcrumb
        CurrentPgTitle="Forums"
        MenuPgTitle="pages"
        img={state.breadcrumbimg}
      />
      <div className="container">
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <h3 className="widget-title pb-0" style={{ color: "#f40400" }}>
            Our Forums
          </h3>
          <div className="title-shape margin-top-10px"></div>
          <h6 className="mt-5">Feel free to start a new topic </h6>
        </div>
        <div className="topics-button-container mt-5">
          {" "}
          {topics.map((topic, index) => (
            <button
              key={index}
              type="button"
              className={classNames({
                "topics-button": true,
                "topics-button-selected": topic === selectedTopic,
              })}
              onClick={handleTopicClick}
              name={topic}
            >
              {topic}
            </button>
          ))}
        </div>

        <table className="title-table">
          <thead>
            <tr>
              <th className="center-checkbox">
                <input
                  type="checkbox"
                  checked={isAllChecked}
                  onClick={() => setIsAllChecked(!isAllChecked)}
                />
              </th>
              <th colSpan="6">
                <div className="table-controls">
                  <div className="d-flex">
                    <Select
                      className="view-selector"
                      placeholder="View all"
                      options={[
                        {
                          value: 0,
                          label: "View all",
                        },
                        {
                          value: 1,
                          label: "Option 1",
                        },
                        {
                          value: 2,
                          label: "Option 2",
                        },
                      ]}
                      menuPortalTarget={document.body}
                    />
                    <button role="button" className="refresh-button ml-2">
                      <span>
                        <FiRefreshCw />
                      </span>
                    </button>
                  </div>
                  <div>
                  <button role="button" className="refresh-button ml-2">
                    <span>
                      <FiRefreshCw />
                    </span>
                  </button>
                  </div>
                </div>
              </th>
            </tr>
          </thead>
          <tbody>
            {["", "", "", "", "", "", "", "", "", ""].map((el, index) => (
              <tr key={index}>
                <td className="center-checkbox">
                  <input type="checkbox" checked={isAllChecked} />
                </td>
                <td style={{ width: 50 }}>
                  <img width="45" height="45" src={gitlogo} />
                </td>
                <td>Facebook Blueprint</td>
                <td>
                  Newly released courses, holiday marketing tips my name is hel
                  nershing thapa and kera jasto jindagi lado jasto muji
                </td>
                <td style={{ width: 10 }}>*</td>
                <td>Sed scelerisque dui lacus</td>
                <td align="right">Today</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {
        // <Footer />
      }
    </main>
  );
}

export default About;
