import React, { useState } from "react";
import classNames from "classnames";
import GeneralHeader from "../components/common/GeneralHeader";
import Banner5 from "../components/banner/banner5/Banner5";
import ImageBox from "../components/about/ImageBox";
import About2 from "../components/about/About2";
import FunFactsThree from "../components/other/funfacts/FunFactsThree";
import { Link } from "react-router-dom";
import { FiPlus } from "react-icons/fi";
import SectionsHeading from "../components/common/SectionsHeading";
import Testimonial from "../components/sliders/Testimonial";
import HowItWork4 from "../components/hiw/HowItWork4";
import TeamOne from "../components/team/TeamOne";
import Button from "../components/common/Button";
import NewsLetter from "../components/other/cta/NewsLetter";
import Footer from "../components/common/footer/Footer";
import ScrollTopBtn from "../components/common/ScrollTopBtn";
import Breadcrumb from "../components/common/Breadcrumb";
import breadcrumbimg from "../assets/images/bread-bg.jpg";
import sectiondata from "../store/store";

const state = {
  breadcrumbimg: breadcrumbimg,
};

const topics = [
  "Official Management District",
  "Japanese Architecture",
  "Ear-acupuncture",
  "Cupping",
  "Scalp Acupuncture",
  "Reflexology",
  "Tuina",
  "Reiki",
  "Tibetan Medicine",
  "Pathology",
  "Technical Issues",
];

function About() {
  const [selectedTopic, setSelectedTopic] = useState(
    "Official Management District"
  );

  const handleTopicClick = (e) => setSelectedTopic(e.target.name);

  return (
    <main className="about-page">
      {/* Header */}
      <GeneralHeader />
      <Breadcrumb
        CurrentPgTitle="Forums"
        MenuPgTitle="pages"
        img={state.breadcrumbimg}
      />
      <div className="container">
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
          }}
        >
          <h3 className="widget-title pb-0" style={{ color: "#f40400" }}>
            Our Forums
          </h3>
          <div className="title-shape margin-top-10px"></div>
          <h6 className="mt-5">Feel free to start a new topic </h6>
        </div>
        <div className="topics-button-container mt-5">
          {" "}
          {topics.map((topic) => (
            <button
              type="button"
              className={classNames({
                "topics-button": true,
                "topics-button-selected": topic === selectedTopic,
              })}
              onClick={handleTopicClick}
              name={topic}
            >
              {topic}
            </button>
          ))}
        </div>
      </div>
      <Footer />
    </main>
  );
}

export default About;
